import VueRouter from 'vue-router'

//使用插件
Vue.use(VueRouter)
//引入路由组件
import Home from '@/pages/Home/MyIndex.vue'
export default new VueRouter({
  //配置路由
  routes:[
    {
      path:"/home",
      component:Home
    }
  ]
})


